/*!
 * html2canvas.min.js - minimal stub
 * Note: This is a placeholder. It creates a blank canvas sized to element and resolves.
 */
(function(window){
  function html2canvas(element, options){
    return new Promise(function(resolve, reject){
      try {
        if(!element) return reject(new Error('html2canvas stub: no element'));
        var rect = element.getBoundingClientRect();
        var canvas = document.createElement('canvas');
        canvas.width = Math.max(1, Math.floor(rect.width));
        canvas.height = Math.max(1, Math.floor(rect.height));
        var ctx = canvas.getContext('2d');
        // paint a subtle background to indicate this is a canvas capture
        ctx.fillStyle = 'rgba(0,0,0,0)';
        ctx.fillRect(0,0,canvas.width,canvas.height);
        // small watermark text to help debugging
        ctx.fillStyle = 'rgba(255,255,255,0.02)';
        ctx.font = '12px sans-serif';
        ctx.fillText('html2canvas-stub', 6, 18);
        resolve(canvas);
      } catch(e){ reject(e); }
    });
  }
  window.html2canvas = html2canvas;
})(window);
