/*!
 * lucide.min.js - tiny shim
 * Provides lucide.createIcons() as no-op and basic icon element replacement.
 */
(function(window){
  var lucide = {
    createIcons: function(){ 
      // scan for [data-lucide] and replace with small inline SVG placeholder
      var els = document.querySelectorAll('[data-lucide]');
      els.forEach(function(el){
        var name = el.getAttribute('data-lucide') || 'icon';
        // tiny square svg
        var svg = '<svg width="16" height="16" viewBox="0 0 24 24" fill="none" stroke="currentColor"><rect x="3" y="3" width="18" height="18" rx="2" ry="2"/></svg>';
        el.innerHTML = svg;
      });
    }
  };
  window.lucide = lucide;
})(window);
